import React from "react";

const Alert = (alert) => {
  return (
    <div
      className={`alert alert-${
        alert.type || "secondary"
      } alert-dismissible fade show`}
      role="alert"
    >
      {alert.text}
      <button
        onClick={() => console.log(alert)}
        type="button"
        className="btn-close"
        aria-label="Close"
      ></button>
    </div>
  );
};

export default Alert;
