import React from "react";

const Alert = (props) => {
  return (
    <div
      className={`alert alert-${
        alert.type || "secondary"
      } alert-dismissible fade show`}
      role="alert"
    >
      {alert.text}
      <button type="button" className="close" aria-label="Close">
        <span>&times;</span>
      </button>
    </div>
  );
};

export default Alert;
